for t, move in enumerate(transcript):
    start, end = tuple(move[0]), tuple(move[-1])
    p = permuted_opts[start]  # opts to move
    assert p.jumped >= t
    p.opts -= {-sign, -2 * sign, 0}
    if any((y2 - y1) * sign < 0 for (x1, y1), (x2, y2) in zip(move, move[1:])):  # backward move!
        if p.promoted >= t:
