        if abs(x2 - x1) == 2:  # jump
            mid = ((x1 + x2) // 2, (y1 + y2) // 2)
            assert permuted_opts[mid].jumped >= t
            permuted_opts[mid].opts -= {0, sign, 2 * sign}  # Can only jump over piece of opposite sign
